    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
int xNameErr, xPwdErr;
xNameErr = xPwdErr = 0;
String xName = txtName.getText();
String xPwd = pwd1.getText();
if(xName.trim().length()==0)
{JOptionPane.showMessageDialog(null,"The name cannot be empty!");
 xNameErr = 1;   
}
if(xPwd.trim().length()<10)
{JOptionPane.showMessageDialog(null,"The password length must be >= 10!");
 xPwdErr = 1;   
}
if(xNameErr==1)
{
 txtName.requestFocus();
    
}
else if (xPwdErr==1)
{
 pwd1.requestFocus();
}
else
{
    
}

// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

